const horas=document.getElementById("horas"),countdownNatal=document.getElementById("countdownNatal"),CountDownToNatal=()=>{const t=new Date,e=t.getTime(),a=new Date(`Dec 25, ${t.getFullYear()} 00:00:00`).getTime(),n=`${t.getDate()}/${t.getMonth()+1}/${t.getFullYear()}`;horas.innerHTML=`<h3><b>Data de hoje:</b> ${n}</h3>`;const o=a-e;if(o<=0)return countdownNatal.innerHTML="<h2>JÃ¡ estamos no natal!</h2>";let l=Math.floor(o/864e5),h=Math.floor(o%864e5/36e5),r=Math.floor(o%36e5/6e4),s=Math.floor(o%6e4/1e3);countdownNatal.innerHTML=`<h2>Faltam <span>${l}d:${h}h:${r}m:${s}s</span> para o natal!</h2>`};setInterval(()=>{CountDownToNatal()},1e3);